# Binary feature if 'client' keywords
def is_client_keywords(url):

    c = "client"

    if c in url:
        return 1
    else:
        return 0

# Binary feature if 'server' keywords
def is_server_keywords(url):

    s = "server"

    if s in url:
        return 1
    else:
        return 0

# Is IP in URL hostanme?
# If IP not in host-name, return 0; else 1
def is_ip_hostname(url):

    hostname = urlparse(url).netloc
    for i in hostname:
        if i.isdigit() == False:
            return 0
    else:
        return 1

# Is PTR record for the host?
# Does PTR resolve to one of the IP address?
def is_ptr(url):
    hostname = urlparse(url).netloc
    try:
        ip = socket.gethostbyname(hostname)
        try:

            domain_address = reversename.from_address(ip)
            domain_name = dns_resolver.query(domain_address, "PTR")[0].to_text()[:-1]
            if len(domain_name) is not 0:
                return 1
            else:
                return 0
        except:
            return 0
    except socket.gaierror:
        return 0
    except UnicodeError:
        return 0


